{
  "id": "evt_test_webhook_{{$timestamp}}",
  "object": "event",
  "api_version": "2023-10-16",
  "created": {{$timestamp}},
  "type": "payment_intent.payment_failed",
  "data": {
    "object": {
      "id": "pi_test_{{$randomUUID}}",
      "object": "payment_intent",
      "amount": 2999,
      "amount_received": 0,
      "currency": "usd",
      "status": "requires_payment_method",
      "client_secret": "pi_test_secret_{{$randomUUID}}",
      "last_payment_error": {
        "code": "card_declined",
        "decline_code": "generic_decline",
        "message": "Your card was declined.",
        "type": "card_error",
        "param": null
      },
      "metadata": {
        "cart_id": "{{cart_id}}",
        "resource_id": "{{cart_id}}"
      },
      "payment_method": "pm_test_{{$randomUUID}}",
      "payment_method_types": ["card"],
      "created": {{$timestamp}},
      "livemode": false
    }
  },
  "livemode": false,
  "pending_webhooks": 1,
  "request": {
    "id": "req_test_{{$randomUUID}}",
    "idempotency_key": null
  }
}

{
  "id": "evt_test_webhook_{{$timestamp}}",
  "object": "event",
  "api_version": "2023-10-16",
  "created": {{$timestamp}},
  "type": "checkout.session.completed",
  "data": {
    "object": {
      "id": "cs_test_{{$randomUUID}}",
      "object": "checkout.session",
      "amount_total": 2999,
      "currency": "usd",
      "customer": "cus_test_{{$randomUUID}}",
      "customer_email": "customer@example.com",
      "mode": "payment",
      "payment_intent": "pi_test_{{$randomUUID}}",
      "payment_status": "paid",
      "status": "complete",
      "success_url": "{{storefront_url}}/checkout/success",
      "cancel_url": "{{storefront_url}}/checkout/cancel",
      "metadata": {
        "cart_id": "{{cart_id}}",
        "resource_id": "{{cart_id}}"
      },
      "shipping_details": {
        "address": {
          "city": "New York",
          "country": "US",
          "line1": "123 Main St",
          "line2": null,
          "postal_code": "10001",
          "state": "NY"
        },
        "name": "John Doe"
      },
      "created": {{$timestamp}},
      "livemode": false
    }
  },
  "livemode": false,
  "pending_webhooks": 1,
  "request": {
    "id": "req_test_{{$randomUUID}}",
    "idempotency_key": null
  }
}
